/**
 * check if the current access environment is a Coinbase Wallet environment
 */
export function isCoinbaseWallet (): boolean {
  const { ethereum } = window
  if (typeof ethereum !== 'undefined') {
    return !!ethereum.isCoinbaseWallet
  }
  else {
    return false
  }
}

